var _ = require('underscore');

exports = module.exports = function(args) {
    if(args.length != 1) {
        console.error('Usage: d5 get {key}');
        process.exit(1);
    }
    
    var val = settings[args[0]];
    if(_.isString(val)) { console.log('"' + val + '"'); }
    else { console.log(val); }
};

exports.help = function() {
    console.log('Get the value of a key.')
    console.log('Usage: d5 get {key}')
};
