var yargs = require('yargs').
    boolean('s').alias('s', 'string').describe('s', 'force to translate value as string');

exports = module.exports = function(args) {
    var argv = yargs.parse(args);

    if(argv._.length != 2) {
        console.error('Usage: d5 set {key} {value} [opts ...]');
        process.exit(1);
    }

    var val = argv._[1];
    if(!argv.s) {
        if(val === 'true') { val = true; }
        else if(val === 'false') { val = false; }
        else { }
    } else {
        val = String(val);
    }

    settings[argv._[0]] = val;
    settings.save();
};

exports.help = function() {
    console.log('Set the value of a key.')
    console.log('Usage: d5 get {key} {value} [opts ...]')
    console.log();
    yargs.showHelp();
};